Ossn.register_callback('ossn','init','ossn_profile_birthdate_picker');function ossn_profile_birthdate_picker(){$(document).ready(function(){var cYear=(new Date).getFullYear();var alldays=Ossn.Print('datepicker:days');var shortdays=alldays.split(",");var allmonths=Ossn.Print('datepicker:months');var shortmonths=allmonths.split(",");var datepick_args={changeMonth:true,changeYear:true,dateFormat:'dd/mm/yy',yearRange:'1900:'+cYear,};if(Ossn.isLangString('datepicker:days')){datepick_args['dayNamesMin']=shortdays;}
if(Ossn.isLangString('datepicker:months')){datepick_args['monthNamesShort']=shortmonths;}
var args=Ossn.call_hook('profile','birthdate:input',null,datepick_args);$("input[name='birthdate']").datepicker(args);$('.ui-datepicker').addClass('notranslate');});}
Ossn.ViewLikes=function($post,$type){if(!$type){$type='post';}
Ossn.MessageBox('likes/view?guid='+$post+'&type='+$type);};Ossn.register_callback('ossn','init','ossn_photos_public_js');function ossn_photos_public_js(){$(document).ready(function(){$("#ossn-photos-show-gallery").on('click',function(e){e.preventDefault();$(".ossn-gallery").eq(0).trigger("click");})
if($('.ossn-gallery').length){$(".ossn-gallery").fancybox();}});}Ossn.register_callback('ossn','init','com_contact_form_validator');function com_contact_form_validator(){Ossn.ajaxRequest({url:Ossn.site_url+"action/Contact/contactmail",form:'#contactform',beforeSend:function(request){var failedValidate=0;$('#contactform input, #contactform textarea').filter(function(){if(this.value==""){$(this).addClass('ossn-red-borders');failedValidate++;}else{$(this).removeClass('ossn-red-borders');}});if(failedValidate){Ossn.trigger_message(Ossn.Print('contact:form:message:incomplete'),'error');request.abort();return false;}},callback:function(callback){if(callback['dataerr']){Ossn.trigger_message(Ossn.Print(callback['dataerr']),'error');}else if(callback['success']==1){Ossn.trigger_message(Ossn.Print('contact:form:message:sent'));setTimeout(function(){location.reload();},2000);}}});}
function contentSharingSaveVisitedURL(url){Ossn.PostRequest({url:Ossn.site_url+'shared_content/request/join/communitiy',params:'redirection_url='+url,beforeSend:function(){},callback:function(callback){window.open(Ossn.site_url,'_blank');},});}