<?phpfunction showcase_init() {	ossn_register_page('showcase', 'showcase_pages');	/* 	this picks up components/calendar/plugins/default/css.php 			where css/weather is added the .php to it*/	ossn_extend_view('css/ossn.default', 'css/showcase');/* 	this picks up components/calendar/plugins/default/js.php 			where js/weather is added the .php to it*/			ossn_extend_view('js/opensource.socialnetwork', 'js/showcase');	  if (ossn_isLoggedin()) {       		$icon = ossn_site_url('components/showcase/image/showcase.png');    	ossn_register_sections_menu('newsfeed', array(		    'name' => 'showcase', /* this adds the name to the class thus .menu-section-item-weather */        	'text' => ossn_print('showcase:menu:title'),/* this is taken from the components/showcase/locale/ossn.en.php */        	'url' => ossn_site_url('showcase'),/* this results to the URL to http://yoursite.com/showcase */			/* 	setting 'icon' to true will remove the <i class="fa"></i> with the font-awesome icon*/        	 'icon' => $icon, /*this icon is located in css/showcase. Look up the code in here https://astronautweb.co/snippet/font-awesome/ */		 'section' => 'links'	    	));		    }}function showcase_pages($pages) { if (!ossn_isLoggedin()) {            ossn_error_page();   }$title = ossn_print('showcase:title');   $contents['content'] = ossn_plugin_view('showcase/showcase');   $content = ossn_set_page_layout('contents', $contents);   echo ossn_view_page($title, $content);}ossn_register_callback('ossn', 'init', 'showcase_init');